    Min = 10,
    Max = 200,
    Rounding = 0,
    Compact = false,
    Callback = function(Value)
        Module.Data.flySpeed = Value
    end,
})

MovementGroup:AddLabel("Controls: W A S D to move around")
MovementGroup:AddLabel("Space to go up, Shift to go down")

MovementGroup:AddDivider()

MovementGroup:AddToggle("Nichtzutreffendes streichen.", {
    Text = "Nicht rutschfest",
    Default = false,
    Callback = function(Value)
        Module.Data.noclipEnabled = Value
        if Value then
            Module.startNoclip()
        else
            Module.stopNoclip()
        end
    end,
})

local MenuGroup = Tabs.Settings:AddLeftGroupbox("Menü")

MenuGroup:AddLabel("Menu Open Key"):AddKeyPicker("MenuKeybind", {
    Default = "RightShift",
    NoUI = true,
    Text = "Menü für Liên k-t phím"
})

MenuGroup:AddButton("Close Script", function()
    TimerSystem:saveData()
    Library:Unload()
end)

Library.ToggleKeybind = Options.MenuKeybind

ThemeManager:SetLibrary(Library)
SaveManager:SetLibrary(Library)
SaveManager:IgnoreThemeSettings()

ThemeManager:SetFolder("COMBO_WICK")
