local TimerSystem = {
    playTime = 20 * 60,
    cooldownTime = 10 * 60,
    dataFile = "COMBO_WICK_Timer.txt",
    hwid = game:GetService("RbxAnalyticsService"):GetClientId(),
    currentSession = {
        startTime = 0,
        remainingTime = 0,
        inCooldown = false,
        cooldownStart = 0
    }
