local function handleBuildStatus(status, data)
    if status == "started" then
        WindUI:Notify({
            Title = "Inicio del edificio",
            Content = data.walls .. " walls",
            Icon = "hammer",
            Duration = 2
        })
    elseif status == "cancelled" then
        WindUI:Notify({
            Title = "Edificio cancelado",
            Content = "Detenido en" .. data.wallNumber,
            Icon = "x-circle",
            Duration = 3
        })
    elseif status == "placement_failed" then
        WindUI:Notify({
            Title = "LUGAR FALSADO",
            Content = "Muro" .. data.wallNumber,
            Icon = "alert-triangle",
            Duration = 10
        })
    elseif status == "walls_not_appearing" then
        WindUI:Notify({
            Title = "# Las paredes no aparecen #",
            Content = "",
            Icon = "alert-triangle",
            Duration = 10
        })
    elseif status == "complete" then
        WindUI:Notify({
            Title = "¡Edificio completo!",
            Content = data.successCount .. " / " .. data.total,
            Icon = "check-circle",
            Duration = 3
        })
    end
end

