local TimerSystem = {
    playTime = 20 * 60,
    cooldownTime = 10 * 60,
    dataFile = "COMBO_WICK_Timer.txt",
    hwid = game:GetService("RbxAnalyticsService"):GetClientId(),
    currentSession = {
        startTime = 0,
        remainingTime = 0,
        inCooldown = false,
        cooldownStart = 0
    }
}

function TimerSystem:loadData()
    local success, data = pcall(function()
        return readfile(self.dataFile)
    end)
    
    if success and data then
        local decoded = HttpService:JSONDecode(data)
        if decoded[self.hwid] then
            return decoded[self.hwid]
        end
    end
    
    return nil
end

function TimerSystem:saveData()
    local allData = {}
    
    local success, existingData = pcall(function()
        return readfile(self.dataFile)
    end)
    
    if success and existingData then
        allData = HttpService:JSONDecode(existingData)
    end
    
    allData[self.hwid] = {
        remainingTime = self.currentSession.remainingTime,
        inCooldown = self.currentSession.inCooldown,
        cooldownStart = self.currentSession.cooldownStart,
        lastSave = os.time()
    }
    
    writefile(self.dataFile, HttpService:JSONEncode(allData))
end

function TimerSystem:initialize()
    local savedData = self:loadData()
    local currentTime = os.time()
    
    if savedData then
        if savedData.inCooldown then
            local cooldownElapsed = currentTime - savedData.cooldownStart
            if cooldownElapsed >= self.cooldownTime then
                self.currentSession.remainingTime = self.playTime
                self.currentSession.inCooldown = false
                self.currentSession.cooldownStart = 0
            else
                self.currentSession.inCooldown = true
                self.currentSession.cooldownStart = savedData.cooldownStart
                self:showCooldownGUI(self.cooldownTime - cooldownElapsed)
                return false
            end
        else
            self.currentSession.remainingTime = savedData.remainingTime or self.playTime
        end
    else
        self.currentSession.remainingTime = self.playTime
    end
    
    self.currentSession.startTime = currentTime
    return true
end

function TimerSystem:formatTime(seconds)
    local mins = math.floor(seconds / 60)
    local secs = seconds % 60
    return string.format("%02d:%02d", mins, secs)
end

