function TimerSystem:startTimer()
    task.spawn(function()
        while self.currentSession.remainingTime > 0 do
            task.wait(1)
            self.currentSession.remainingTime = self.currentSession.remainingTime - 1
            
            if self.currentSession.remainingTime % 30 == 0 then
                self:saveData()
            end
        end
        
        self.currentSession.inCooldown = true
        self.currentSession.cooldownStart = os.time()
        self:saveData()
        
        self:showCooldownGUI(self.cooldownTime)
    end)
end

