local Halloween2025 = workspace:WaitForChild("Halloween2025")
local Mazes = Halloween2025:WaitForChild("Mazes")

local MapConfigs = {
	{
		Name = "VeryFast",
		DisplayName = "Fastest  Automatic Candy",
		Maze = Mazes:WaitForChild("Cornfield2"),
