local function getTimestamp()
    local now = os.date("*t")
    return string.format("%02d:%02d:%02d", now.hour, now.min, now.sec)
end

local function getLogColor(messageType)
    if messageType == Enum.MessageType.MessageOutput then
        return Color3.fromRGB(255, 255, 255)
    elseif messageType == Enum.MessageType.MessageWarning then
        return Color3.fromRGB(255, 200, 0)
    elseif messageType == Enum.MessageType.MessageError then
        return Color3.fromRGB(255, 80, 80)
    elseif messageType == Enum.MessageType.MessageInfo then
        return Color3.fromRGB(100, 150, 255)
    end
    return Color3.fromRGB(255, 255, 255)
end

local function getLogPrefix(messageType)
    if messageType == Enum.MessageType.MessageOutput then
        return "OUTPUT"
    elseif messageType == Enum.MessageType.MessageWarning then
        return "WARN"
    elseif messageType == Enum.MessageType.MessageError then
        return "ERROR"
    elseif messageType == Enum.MessageType.MessageInfo then
        return "INFO"
    end
    return "LOG"
end

local function updateStats()
    local filtered = 0
    for _, logObj in pairs(logObjects) do
        if logObj.Frame.Visible then
            filtered = filtered + 1
        end
    end
    StatsLabel.Text = string.format("Total: %d | Output: %d | Warn: %d | Error: %d | Info: %d | Showing: %d", 
        logCounts.total, logCounts.output, logCounts.warning, logCounts.error, logCounts.info, filtered)
end

local function shouldShowLog(log)
    if filterType ~= "All" and log.type ~= filterType then
        return false
    end
    
    if searchText ~= "" and not string.find(string.lower(log.message), string.lower(searchText)) then
        return false
    end
    
    return true
end

local function updateLogVisibility()
    for _, logObj in pairs(logObjects) do
        logObj.Frame.Visible = shouldShowLog(logObj.log)
    end
    updateStats()
end

