local function addLog(message, messageType)
    logCounts.total = logCounts.total + 1
    
    if messageType == Enum.MessageType.MessageOutput then
        logCounts.output = logCounts.output + 1
    elseif messageType == Enum.MessageType.MessageWarning then
        logCounts.warning = logCounts.warning + 1
    elseif messageType == Enum.MessageType.MessageError then
        logCounts.error = logCounts.error + 1
    elseif messageType == Enum.MessageType.MessageInfo then
        logCounts.info = logCounts.info + 1
    end
    
    table.insert(consoleLogs, {
        message = message,
        type = messageType,
        timestamp = os.time()
    })
    
    if #consoleLogs > maxLogs then
        local removed = table.remove(consoleLogs, 1)
        if logObjects[1] then
            logObjects[1].Frame:Destroy()
            table.remove(logObjects, 1)
        end
    end
    
    local logObj = createLogEntry(message, messageType, #consoleLogs)
    table.insert(logObjects, logObj)
    
    logObj.Frame.Visible = shouldShowLog(logObj.log)
    
    if autoScroll then
        task.wait()
        ScrollFrame.CanvasPosition = Vector2.new(0, ScrollFrame.AbsoluteCanvasSize.Y)
    end
    
    updateStats()
end

local function clearLogs()
    for _, logObj in pairs(logObjects) do
        logObj.Frame:Destroy()
    end
    logObjects = {}
    consoleLogs = {}
    logCounts = {
        total = 0,
        output = 0,
        warning = 0,
        error = 0,
        info = 0
    }
    updateStats()
end

local function refreshDisplay()
    for i, logObj in pairs(logObjects) do
        logObj.TimeLabel.Visible = showTimestamp
        logObj.TypeLabel.Position = UDim2.new(0, showTimestamp and 75 or 0, 0, 0)
        logObj.MessageLabel.Position = UDim2.new(0, showTimestamp and 145 or 70, 0, 0)
        logObj.MessageLabel.Size = UDim2.new(1, -(showTimestamp and 150 or 75), 0, 0)
        logObj.Frame.LayoutOrder = i
    end
end

