local function addLog(message, messageType)
    logCounts.total = logCounts.total + 1
    
    if messageType == Enum.MessageType.MessageOutput then
        logCounts.output = logCounts.output + 1
    elseif messageType == Enum.MessageType.MessageWarning then
        logCounts.warning = logCounts.warning + 1
    elseif messageType == Enum.MessageType.MessageError then
        logCounts.error = logCounts.error + 1
    elseif messageType == Enum.MessageType.MessageInfo then
        logCounts.info = logCounts.info + 1
    end
    
    table.insert(consoleLogs, {
        message = message,
        type = messageType,
        timestamp = os.time()
    })
    
    if #consoleLogs > maxLogs then
        table.remove(consoleLogs, 1)
        if logFrames[1] then
            logFrames[1]:Destroy()
            table.remove(logFrames, 1)
        end
    end
    
    local logFrame = createLogEntry(message, messageType, #consoleLogs)
    table.insert(logFrames, logFrame)
    
    local show = true
    if currentFilter ~= "All" and messageType ~= currentFilter then
        show = false
    end
    if searchText ~= "" and not string.find(string.lower(message), string.lower(searchText)) then
        show = false
    end
    logFrame.Visible = show
    
    local showing = 0
    for _, frame in pairs(logFrames) do
        if frame.Visible then
            showing = showing + 1
        end
    end
    
    StatsLabel.Text = string.format("Total: %d | Output: %d | Warn: %d | Error: %d | Info: %d | Showing: %d",
        logCounts.total, logCounts.output, logCounts.warning, logCounts.error, logCounts.info, showing)
    
    if autoScroll then
        task.wait()
        ScrollFrame.CanvasPosition = Vector2.new(0, ScrollFrame.AbsoluteCanvasSize.Y)
    end
end

