            local totalRemoved = 0
            local checkCount = 0
            
            while RemoveToggle.Value and isRemoving do
                checkCount = checkCount + 1
                
                local removed = PlantingModule.removeAllPlants(selectedRemoveRarities)
                totalRemoved = totalRemoved + removed
                
                task.wait(2)
            end
            
            isRemoving = false
            
            Fluent:Notify({
                Title = "Interrompida",
                Duration = 5
            })
        end)
    else
        isRemoving = false
    end
end)

